package org.eclipse.jdt.internal.debug.ui.launcher;/* * (c) Copyright IBM Corp. 2000, 2001. * All Rights Reserved. */import org.eclipse.jdt.launching.AbstractVMInstall;import org.eclipse.jdt.launching.IVMInstall;import org.eclipse.jdt.launching.IVMInstallType;/** * A VMInstall class that used for manipulating the set of vm installs * withouth committing early. */public class VMStandin extends AbstractVMInstall {	/**	 * Constructor for VMStandin	 */	public VMStandin(IVMInstallType type, String id) {		super(type, id);	}		public VMStandin(IVMInstall realVM) {		this (realVM.getVMInstallType(), realVM.getId());		setName(realVM.getName());		setInstallLocation(realVM.getInstallLocation());		setLibraryLocations(realVM.getLibraryLocations());		setJavadocLocation(realVM.getJavadocLocation());	}		public IVMInstall convertToRealVM() {		IVMInstallType vmType= getVMInstallType();		IVMInstall realVM= vmType.findVMInstall(getId());				if (realVM == null) {			realVM= vmType.createVMInstall(getId());		}				realVM.setName(getName());		realVM.setInstallLocation(getInstallLocation());		realVM.setLibraryLocations(getLibraryLocations());		realVM.setJavadocLocation(getJavadocLocation());		return realVM;	}}